import * as R from 'ramda';
import {
  FETCH_PHONES_SUCCESS,
  LOAD_MORE_PHONES_SUCCESS,
  FFETCH_PHONE_BY_ID_SUCCESS
} from 'actionTypes';

const initialState = {}

const phonesReducer = (state = initialState, {type, payload }) => {
  switch (type) {
    case FETCH_PHONES_SUCCESS:
      const newValue = R.indexBy(R.prop('id'), payload);
      return R.mergeRight(state, newValue); // { ...state, newValue}

    case LOAD_MORE_PHONES_SUCCESS:
      const moreValue = R.indexBy(R.prop('id'), payload);
      return R.mergeRight(state, moreValue); // { ...state, moreValue}

    case FFETCH_PHONE_BY_ID_SUCCESS:
      return R.assoc(payload.id, payload, state)
    default:
      return state;
  }
}

export default phonesReducer;